import { useState, useEffect, useContext } from "react";
import {
  Button,
  Chip,
  Grid,
  IconButton,
  InputAdornment,
  Paper,
  TextField,
  Typography,
  Modal,
  Slider,
  FormControlLabel,
  FormGroup,
  MenuItem,
  Checkbox,
  styled,
} from "@mui/material";
import Rating from "@mui/material/Rating";
import Pagination from "@mui/material/Pagination";
import axios from "axios";
import SearchIcon from "@mui/icons-material/Search";
import FilterListIcon from "@mui/icons-material/FilterList";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";

import { SetPopupContext } from "../App";

import apiList from "../lib/apiList";
import { userType } from "../lib/isAuth";

const JobTileOuter = styled(Paper)(({ theme }) => ({
  padding: "30px",
  margin: "0px 0",
  boxSizing: "border-box",
  width: "100%",
}));

const PopupDialog = styled(Paper)(({ theme }) => ({
  height: "100%",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const useStyles = makeStyles((theme) => ({
  body: {
    height: "inherit",
  },
  button: {
    width: "50",
    height: "100%",
  },
}));

const JobTile = (props) => {
  const classes = useStyles();
  const { job } = props;
  const setPopup = useContext(SetPopupContext);

  const [open, setOpen] = useState(false);
  const [sop, setSop] = useState("");

  const handleClose = () => {
    setOpen(false);
    setSop("");
  };

  const handleApply = () => {
    console.log(job._id);
    console.log(sop);
    axios
      .post(
        `${apiList.jobs}/${job._id}/applications`,
        {
          sop: sop,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      )
      .then((response) => {
        setPopup({
          open: true,
          severity: "success",
          message: response.data.message,
        });
        handleClose();
      })
      .catch((err) => {
        console.log(err.response);
        setPopup({
          open: true,
          severity: "error",
          message: err.response.data.message,
        });
        handleClose();
      });
  };

  const deadline = new Date(job.deadline).toLocaleDateString();

  return (
    <Grid container>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <JobTileOuter elevation={3}>
            <Grid container>
              <Grid container item xs={9} spacing={1} direction="column">
                <Grid item>
                  <Typography variant="h5">{job.title}</Typography>
                </Grid>
                <Grid item>Posted By: <b>{job.recruiter.Companyname}</b></Grid>
                <Grid item>Location: <b>{job.location}</b></Grid>
              </Grid>
              <Grid item xs={3}>
                <Button
                  variant="contained"
                  color="primary"
                  style={{
                    width: 100,
                    marginTop: 25,
                  }}
                  onClick={() => {
                    setOpen(true);
                  }}
                  disabled={userType() === "recruiter"}
                >
                  Apply
                </Button>
              </Grid>
            </Grid>
            <Modal open={open} onClose={handleClose}>
              <PopupDialog>
                <Grid container spacing={2} direction="column" style={{ width: 400 }}>
                  <Grid item>
                    <TextField
                      multiline
                      rows={4}
                      fullWidth
                      variant="outlined"
                      label="Statement of Purpose"
                      value={sop}
                      onChange={(e) => setSop(e.target.value)}
                    />
                  </Grid>
                  <Grid item>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={handleApply}
                      fullWidth
                    >
                      Apply
                    </Button>
                  </Grid>
                </Grid>
              </PopupDialog>
            </Modal>
          </JobTileOuter>
        </Grid>
      </Grid>
    </Grid>
  );
};

const JobList = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    axios.get(apiList.jobs).then((response) => {
      setJobs(response.data.jobs);
    });
  }, []);

  return (
    <Grid container spacing={2} direction="column">
      {jobs.map((job) => (
        <Grid item key={job._id}>
          <JobTile job={job} />
        </Grid>
      ))}
    </Grid>
  );
};

const SortButton = styled(Button)(({ theme }) => ({
  padding: "10px 15px",
  borderRadius: "50px",
}));

const JobDetails = ({ jobId }) => {
  const [job, setJob] = useState(null);

  useEffect(() => {
    axios.get(`${apiList.jobs}/${jobId}`).then((response) => {
      setJob(response.data.job);
    });
  }, [jobId]);

  if (!job) {
    return <div>Loading...</div>;
  }

  return (
    <JobTileOuter elevation={3}>
      <Typography variant="h4">{job.title}</Typography>
      <Typography variant="h6">
        Posted By: <b>{job.recruiter.Companyname}</b>
      </Typography>
      <Typography variant="h6">
        Location: <b>{job.location}</b>
      </Typography>
      {/* Render other job details */}
    </JobTileOuter>
  );
};

const SortJobs = () => {
  const [sortOrder, setSortOrder] = useState("asc");

  const handleSortChange = () => {
    setSortOrder(sortOrder === "asc" ? "desc" : "asc");
  };

  return (
    <SortButton
      variant="contained"
      color="primary"
      startIcon={sortOrder === "asc" ? <ArrowUpwardIcon /> : <ArrowDownwardIcon />}
      onClick={handleSortChange}
    >
      Sort {sortOrder === "asc" ? "Ascending" : "Descending"}
    </SortButton>
  );
};

const FilterJobs = () => {
  const [filterOptions, setFilterOptions] = useState({
    location: "",
    type: "",
    duration: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFilterOptions((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <TextField
        name="location"
        label="Location"
        variant="outlined"
        value={filterOptions.location}
        onChange={handleChange}
      />
      <TextField
        name="type"
        label="Type"
        variant="outlined"
        value={filterOptions.type}
        onChange={handleChange}
      />
      <TextField
        name="duration"
        label="Duration"
        variant="outlined"
        value={filterOptions.duration}
        onChange={handleChange}
      />
    </div>
  );
};

const JobFilters = () => {
  return (
    <Grid container spacing={2}>
      <Grid item>
        <SortJobs />
      </Grid>
      <Grid item>
        <FilterJobs />
      </Grid>
    </Grid>
  );
};

const JobsPage = () => {
  const [page, setPage] = useState(1);

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  return (
    <div>
      <JobFilters />
      <JobList />
      <Pagination count={10} page={page} onChange={handlePageChange} />
    </div>
  );
};

export default JobsPage;
