import { useState, useEffect, useContext } from "react";
import {
  Button,
  Chip,
  Grid,
  IconButton,
  InputAdornment,

  Paper,
  TextField,
  Typography,
  Modal,
  Slider,
  FormControlLabel,
  FormGroup,
  MenuItem,
  Checkbox,
} from "@mui/material";

import { makeStyles } from "@material-ui/core";
import Rating from "@material-ui/lab/Rating";
import axios from "axios";

import { SetPopupContext } from "../App";

import apiList from "../lib/apiList";
import SideComponent from "./SideBar";

const useStyles = makeStyles((theme) => ({
  body: {
    height: "inherit",
  },
  statusBlock: {
    width: "100%",
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    textTransform: "uppercase",
  },
  jobTileOuter: {
    padding: "30px",
    margin: "20px 0",
    boxSizing: "border-box",
    width: "100%",
  },
  popupDialog: {
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
}));

const ApplicationTile = (props) => {
  const classes = useStyles();
  const { application } = props;
  const setPopup = useContext(SetPopupContext);
  const [open, setOpen] = useState(false);
  const [rating, setRating] = useState(application.job.rating);

  const appliedOn = new Date(application.dateOfApplication);
  const joinedOn = new Date(application.dateOfJoining);

  const fetchRating = () => {
    axios
      .get(`${apiList.rating}?id=${application.job._id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((response) => {
        setRating(response.data.rating);
        console.log(response.data);
      })
      .catch((err) => {
        // console.log(err.response);
        console.log(err.response.data);
        setPopup({
          open: true,
          severity: "error",
          message: "Error",
        });
      });
  };

  const changeRating = () => {
    axios
      .put(
        apiList.rating,
        { rating: rating, jobId: application.job._id },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      )
      .then((response) => {
        console.log(response.data);
        setPopup({
          open: true,
          severity: "success",
          message: "Rating updated successfully",
        });
        fetchRating();
        setOpen(false);
      })
      .catch((err) => {
        // console.log(err.response);
        console.log(err);
        setPopup({
          open: true,
          severity: "error",
          message: err.response.data.message,
        });
        fetchRating();
        setOpen(false);
      });
  };

  const handleClose = () => {
    setOpen(false);
  };

  const colorSet = {
    applied: "#3454D1",
    shortlisted: "#DC851F",
    accepted: "#09BC8A",
    rejected: "#D1345B",
    deleted: "#B49A67",
    cancelled: "#d9534f",
    finished: "#4EA5D9",
  };

  return (
    <Grid container >

      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Paper className={classes.jobTileOuter} elevation={3}>
            <Grid container>
              <Grid container item xs={9} spacing={1} direction="column">
                <Grid item>
                  <Typography variant="h5">{application.job.title}</Typography>
                </Grid>
                {/* <Grid item>
            <Rating value={job.rating !== -1 ? job.rating : null} readOnly />
          </Grid> */}
                <Grid item>Posted By : <b>{job.recruiter.Companyname}</b></Grid>
                <Grid item>Location : <b>{job.location}</b></Grid>
                {/* <Grid item>Role : {job.jobType}</Grid> */}
                {/* <Grid item>Salary : &#8377; {job.salary} per month</Grid> */}
                {/* <Grid item>
            Duration :{" "}
            {job.duration !== 0 ? `${job.duration} month` : `Flexible`}
          </Grid>
          <Grid item>Application Deadline : {deadline}</Grid>

          <Grid item>
            skillsets reqired :{" "}
            {job.skillsets.map((skill) => (
              <Chip label={skill} style={{ marginRight: "2px",backgroundColor:"#1976d2", color:"white" }} />
            ))}
          </Grid> */}
              </Grid>
              <Grid item xs={3}>
                <Button
                  variant="contained"
                  color="primary"
                  style={{
                    width: 100,
                    marginTop: 25,
                    // margin:50,

                  }}
                  // className={classes.button}
                  onClick={() => {
                    setOpen(true);
                  }}

                  disabled={userType() === "recruiter"}

                >
                  Apply
                </Button>
              </Grid>
            </Grid>
            <Modal open={open} onClose={handleClose} className={classes.popupDialog}>
              <Paper
                style={{
                  padding: "20px",
                  outline: "none",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "center",
                  minWidth: "50%",
                  alignItems: "center",
                }}
              >
                <TextField
                  label="Write SOP (upto 250 words)"
                  multiline
                  rows={8}
                  style={{ width: "100%", marginBottom: "30px" }}
                  variant="outlined"
                  value={sop}
                  onChange={(event) => {
                    if (
                      event.target.value.split(" ").filter(function (n) {
                        return n != "";
                      }).length <= 250
                    ) {
                      setSop(event.target.value);
                    }
                  }}
                />
                <Button
                  variant="contained"
                  color="primary"
                  style={{ padding: "10px 50px" }}
                  onClick={() => handleApply()}
                >
                  Submit
                </Button>
              </Paper>
            </Modal>
          </Paper>
        </Grid>

      </Grid>
    </Grid>


  );
};

const Applications = (props) => {
  const setPopup = useContext(SetPopupContext);
  const [applications, setApplications] = useState([]);
  const [singleJob,setSingleJob]=useState();

  useEffect(() => {
    getData();
  }, []);

  // useEffect(() => {
  //   setSingleJob(jobs[0]);
  //   console.log(singleJob);
  // }, [jobs[0]]);

  const changeJob=(newJob)=>{
    setSingleJob(newJob);
    console.log(singleJob);
  }

  const getData = () => {
    axios
      .get(apiList.applications, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((response) => {
        console.log(response.data);
        setApplications(response.data);
      })
      .catch((err) => {
        // console.log(err.response);
        console.log(err.response.data);
        setPopup({
          open: true,
          severity: "error",
          message: "Error",
        });
      });
  };

  return (
    <Grid
      container
      item
      direction="column"
      alignItems="center"
      style={{ padding: "30px", minHeight: "93vh" }}
    >
      <Grid item>
        <Typography variant="h2">Applications</Typography>
      </Grid>
      <Grid item xs={12} container>
        <Grid
          container
          item
          xs={5}
          // direction="column"
          // style={{ width: "100%" }}
          alignItems="stretch"
          justify="center"
        >
          {applications.length > 0 ? (
            applications.map((obj) => (
              <Grid item>
                <ApplicationTile application={obj} />
              </Grid>
            ))
          ) : (
            <Typography variant="h5" style={{ textAlign: "center" }}>
              No Applications Found
            </Typography>
          )}
        </Grid>
        <Grid item xs={7}>
          <SideComponent style={{ backgroundColor: 'pink' }} />
          <p style={{ backgroundColor: 'pink' }}>mvbfdhbfdhjvbfdhbvfdjhvbfdhbv</p>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Applications;
